mapper.select:
SELECT DISTINCT t.* FROM task t INNER JOIN TaskReciver tr ON t.reciverId = tr.taskNum INNER JOIN `member` m ON tr.reciverId = m.id INNER JOIN taskpart tp ON m.departmentId = tp.id
WHERE t.title LIKE CONCAT('%',#{search},'%') OR t.body LIKE CONCAT('%',#{search},'%')
WHERE t.title LIKE CONCAT('%',#{search},'%')
WHERE t.body LIKE CONCAT('%',#{search},'%')
WHERE (t.title LIKE CONCAT('%',#{search},'%') OR t.body LIKE CONCAT('%',#{search},'%')) AND m.departmentId=#{taskPartId}
WHERE t.title LIKE CONCAT('%',#{search},'%') AND m.departmentId=#{taskPartId}
WHERE t.body LIKE CONCAT('%',#{search},'%') AND m.departmentId=#{taskPartId}
ORDER BY t.id DESC LIMIT #{startId}, 5
